import React, { useEffect, useState } from "react";
import styled from "styled-components";

const OrdersBackground = styled.div`
  height: 100vh;
  width: 100vw;
  display: flex;
  justify-content: center;
`;

const OrdersWrapper = styled.div`
  margin-top: 5rem;
  width: 80vw;
  min-height: 40vh;
  max-height: fit-content;
  background-color: white;
  display: block;
  overflow: auto;
`;

const OrdersHeader = styled.header`
  padding: 1.5rem 1.5rem;
  font-family: "Roboto", sans-serif;
`;

type Order = {
  Id: string;
  storeId: string;
  storeName?: string;
  storeCountry?: string;
  orderId: string;
  latest_ship_date: string;
  shipment_status: string;
  destination: string;
  items: string;
  orderValue: string;
  marketplace?: string;
}

const getFlagEmoji = (country?: string): string => {
  switch (country) {
    case "AUS":
      return 'ðŸ‡¦ðŸ‡º';
    case "GBR":
      return 'ðŸ‡¬ðŸ‡§';
    case "USA":
      return 'ðŸ‡ºðŸ‡¸';
    default:
      return '';
  }
};

export const OverdueOrders = () => {
  const [overdueOrders, setOverdueOrders] = useState<Order[] | null>(null);

  useEffect(() => {
    fetch("http://localhost:8080/orders")
      .then(results => results.json())
      .then(data => {
        setOverdueOrders(data)
      });

  }, []);

  return (
    <OrdersBackground>
      <OrdersWrapper>
        <OrdersHeader>Overdue Orders</OrdersHeader>

      </OrdersWrapper>
    </OrdersBackground>
  );

}
