import React, { useState } from "react";
import styled from "styled-components";
import { Order } from "./OverdueOrders";

const StyledTableList = styled.table`
  width: 100%;
  font-family: "Roboto", sans-serif;
  text-align: left;
  border-collapse: collapse;
  th {
    background-color: #f2f5f9;
    color: #888888;
    font-weight: light;
    font-size: small;
  }
  td, th {
    border: 1px solid #f0f0f0;
    border-left: none;
    border-right: none;
    padding-top: 20px;
    padding-bottom: 20px;
    padding-left: 20px;
  }
`;

type OrdersTableProps = {
  orderItems: Order[] | null;
  itemsPerPage: number;
}

const getFlagEmoji = (country?: string): string => {
  switch (country) {
    case "AUS":
      return 'ðŸ‡¦ðŸ‡º';
    case "GBR":
      return 'ðŸ‡¬ðŸ‡§';
    case "USA":
      return 'ðŸ‡ºðŸ‡¸';
    default:
      return '';
  }
};

export const OrdersTable = ({ orderItems, itemsPerPage }: OrdersTableProps) => {
  const [currentPage, setCurrentPage] = useState(0);
  const [totalPages, setTotalPages] = useState(orderItems && Math.ceil(orderItems?.length / itemsPerPage));

  const startIndex = currentPage * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;

  return (
    <StyledTableList>
      <thead>
        <tr>
          <th>MARKETPLACE</th>
          <th>STORE</th>
          <th>ORDER ID</th>
          <th>ORDER VALUE</th>
          <th>ITEMS</th>
          <th>DESTINATION</th>
          <th>DAYS OVERDUE</th>
        </tr>
      </thead>
      <tbody>
        {orderItems && orderItems.map(orderItem => (
          <tr>
            <td>{getFlagEmoji(orderItem.storeCountry)} {orderItem.marketplace}</td>
            <td>{orderItem.storeName}</td>
            <td>{orderItem.orderId}</td>
            <td>
              {
                new Intl.NumberFormat(
                  'en-AU',
                  { style: 'currency', currency: 'AUD' }
                )
                .format(Number(orderItem.orderValue))
              }
            </td>
            <td>{orderItem.items}</td>
            <td>{orderItem.destination}</td>
            <td>{orderItem.latest_ship_date}</td>
          </tr>
        ))}
      </tbody>
    </StyledTableList>
  );
}
