import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { Order } from "./OverdueOrders";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const StyledTableList = styled.table`
  width: 100%;
  font-family: "Roboto", sans-serif;
  text-align: left;
  border-collapse: collapse;
  th {
    background-color: #f2f5f9;
    color: #888888;
    font-weight: light;
    font-size: small;
  }
  td, th {
    border: 1px solid #f0f0f0;
    border-left: none;
    border-right: none;
    padding-top: 20px;
    padding-bottom: 20px;
    padding-left: 20px;
  }
`;

const PaginateList = styled.ul`
  display: flex;
  justify-content: right;
  list-style: none;
  font-family: "Roboto", sans-serif;
  li {
    margin: 5px;
    padding: 5px;
  }
  button {
    width: 100%;
    height: 100%;
    background-color: white;
    border: 2px solid white;
    border-radius: 5px;
  }
  .pageNumber {
    padding: 7px;
    border: 2px solid #cacaca;
  }
  .active {
    background-color: aliceblue;
    width: 40px;
  }
`;

type OrdersTableProps = {
  orderItems: Order[];
  itemsPerPage: number;
  handleSort: any;
}

const getFlagEmoji = (country?: string): string => {
  switch (country) {
    case "AUS":
      return 'ðŸ‡¦ðŸ‡º';
    case "GBR":
      return 'ðŸ‡¬ðŸ‡§';
    case "USA":
      return 'ðŸ‡ºðŸ‡¸';
    default:
      return '';
  }
};

type PaginateProps = {
  totalPages: number;
  onPageChange: any;
  currentPage: number;
};

const Paginate = ({
  totalPages,
  onPageChange,
  currentPage
}: PaginateProps) => {
  return (
    <PaginateList>
      <li>
        <button
          disabled={currentPage === 0}
          onClick={() => onPageChange({ selected: 0 })}
        >
          {"<<"}
        </button>
      </li>
      <li>
        <button
          type="button"
          disabled={currentPage === 0}
          onClick={() => onPageChange({ selected: currentPage - 1 })}
        >
          {"<"}
        </button>
      </li>
      {Array.from({ length: totalPages })
        .map((_item, i) => (
          <li key={i}>
            <button
              className={`pageNumber ${currentPage === i && 'active'}`}
              type="button"
              onClick={() => onPageChange({ selected: i })}
            >
              {i + 1}
            </button>
          </li>
        ))
      }
      <li>
        <button
          type="button"
          disabled={currentPage === totalPages - 1}
          onClick={() => onPageChange({ selected: currentPage + 1 })}
        >
          {">"}
        </button>
      </li>
      <li>
        <button
          type="button"
          disabled={currentPage === totalPages - 1}
          onClick={() => onPageChange({ selected: totalPages - 1 })}
        >
          {">>"}
        </button>
      </li>
    </PaginateList>
  );
};

export const OrdersTable = ({ orderItems, itemsPerPage, handleSort }: OrdersTableProps) => {
  const [currentPage, setCurrentPage] = useState(0);
  const [totalPages, setTotalPages] = useState(0);
  const [subset, setSubset] = useState<Order[]>([]);

  const startIndex = currentPage * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;

  useEffect(() => {
    setTotalPages(Math.ceil(orderItems.length / itemsPerPage))
    setSubset(orderItems.slice(startIndex, endIndex));
  }, [itemsPerPage, orderItems, endIndex, startIndex]);

  const handlePageChange = (selectedPage: any) => {
    setCurrentPage(selectedPage.selected);
  };

  return (
    <>
      <StyledTableList>
        <thead>
          <tr>
            <th>MARKETPLACE</th>
            <th>STORE</th>
            <th>ORDER ID</th>
            <th>ORDER VALUE</th>
            <th>ITEMS</th>
            <th>DESTINATION</th>
            <th>DAYS OVERDUE <button onClick={handleSort}><FontAwesomeIcon icon={faSort} /></button></th>
          </tr>
        </thead>
        <tbody>
          {subset && subset.map(orderItem => (
            <tr key={orderItem.Id}>
              <td>{getFlagEmoji(orderItem.storeCountry)} {orderItem.marketplace}</td>
              <td>{orderItem.storeName}</td>
              <td>{orderItem.orderId}</td>
              <td>
                {
                  new Intl.NumberFormat(
                    'en-AU',
                    { style: 'currency', currency: 'AUD' }
                  )
                  .format(Number(orderItem.orderValue))
                }
              </td>
              <td>{orderItem.items}</td>
              <td>{orderItem.destination}</td>
              <td>{orderItem.daysOverdue}</td>
            </tr>
          ))}
        </tbody>
      </StyledTableList>
      <Paginate totalPages={totalPages} onPageChange={handlePageChange} currentPage={currentPage} />
    </>
  );
}
