import React, { useEffect, useState } from "react";

import styled from "styled-components";

const OrdersBackground = styled.div`
  height: 100vh;
  width: 100vw;
  display: flex;
  justify-content: center;
`;

const OrdersWrapper = styled.div`
  margin-top: 5rem;
  width: 80vw;
  min-height: 40vh;
  max-height: fit-content;
  background-color: white;
  display: block;
  overflow: auto;
`;

const OrdersHeader = styled.header`
  padding: 1.5rem 1.5rem;
  font-family: "Roboto", sans-serif;
`;

const OrdersTable = styled.table`
  width: 100%;
  font-family: "Roboto", sans-serif;
  text-align: left;
  border-collapse: collapse;
  th {
    background-color: #f2f5f9;
    color: #888888;
    font-weight: light;
    font-size: small;
  }
  td, th {
    border: 1px solid #f0f0f0;
    border-left: none;
    border-right: none;
    padding-top: 20px;
    padding-bottom: 20px;
    padding-left: 20px;
  }
`;

type Order = {
  Id: string;
  storeId: string;
  storeName?: string;
  storeCountry?: string;
  orderId: string;
  latest_ship_date: string;
  shipment_status: string;
  destination: string;
  items: string;
  orderValue: string;
  marketplace?: string;
}

const getFlagEmoji = (country?: string): string => {
  switch (country) {
    case "AUS":
      return 'ðŸ‡¦ðŸ‡º';
    case "GBR":
      return 'ðŸ‡¬ðŸ‡§';
    case "USA":
      return 'ðŸ‡ºðŸ‡¸';
    default:
      return '';
  }
};

export const OverdueOrders = () => {
  const [overdueOrders, setOverdueOrders] = useState<Order[] | null>(null);

  useEffect(() => {
    fetch("http://localhost:8080/orders")
      .then(results => results.json())
      .then(data => {
        setOverdueOrders(data)
      });

  }, []);

  return (
    <OrdersBackground>
      <OrdersWrapper>
        <OrdersHeader>Overdue Orders</OrdersHeader>
        <OrdersTable>
          <thead>
            <tr>
              <th>MARKETPLACE</th>
              <th>STORE</th>
              <th>ORDER ID</th>
              <th>ORDER VALUE</th>
              <th>ITEMS</th>
              <th>DESTINATION</th>
              <th>DAYS OVERDUE</th>
            </tr>
          </thead>
          <tbody>
            {overdueOrders && overdueOrders.map(overdueOrder => (
              <tr>
                <td>{getFlagEmoji(overdueOrder.storeCountry)} {overdueOrder.marketplace}</td>
                <td>{overdueOrder.storeName}</td>
                <td>{overdueOrder.orderId}</td>
                <td>
                  {
                    new Intl.NumberFormat(
                      'en-AU',
                      { style: 'currency', currency: 'AUD' }
                    )
                    .format(Number(overdueOrder.orderValue))
                  }
                </td>
                <td>{overdueOrder.items}</td>
                <td>{overdueOrder.destination}</td>
                <td>{overdueOrder.latest_ship_date}</td>
              </tr>
            ))}
          </tbody>
        </OrdersTable>
      </OrdersWrapper>
    </OrdersBackground>
  );

}
