import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { OrdersTable } from "./OrdersTable";

const OrdersBackground = styled.div`
  height: 100vh;
  width: 100vw;
  display: flex;
  justify-content: center;
  background-color: #cccccc;
`;

const OrdersWrapper = styled.div`
  margin-top: 5rem;
  width: 80vw;
  min-height: 40vh;
  max-height: fit-content;
  background-color: white;
  display: block;
  overflow: auto;
`;

const OrdersHeader = styled.header`
  padding: 1.5rem 1.5rem;
  font-family: "Roboto", sans-serif;
`;

export type Order = {
  Id: string;
  storeId: string;
  storeName: string;
  storeCountry: string;
  orderId: string;
  latest_ship_date: string;
  shipment_status: string;
  destination: string;
  items: string;
  orderValue: string;
  marketplace: string;
  daysOverdue: number;
}

export const OverdueOrders = () => {
  const [overdueOrders, setOverdueOrders] = useState<Order[]>([]);
  const [filterToggle, setFilterToggle] = useState(1);

  useEffect(() => {
    fetch("http://localhost:8080/orders")
      .then(results => results.json())
      .then(data => setOverdueOrders(data));
  }, []);

  const handleSort = () => {
    if (filterToggle % 2 === 0) {
      setOverdueOrders(overdueOrders?.sort((a, b) => b.daysOverdue - a.daysOverdue));
    } else {
      setOverdueOrders(overdueOrders?.sort((a, b) => a.daysOverdue - b.daysOverdue));
    }
    setFilterToggle(filterToggle + 1);
  };

  return (
    <OrdersBackground>
      <OrdersWrapper>
        <OrdersHeader>Overdue Orders</OrdersHeader>
        <OrdersTable orderItems={overdueOrders} itemsPerPage={5} handleSort={handleSort} />
      </OrdersWrapper>
    </OrdersBackground>
  );

}
